import chai = require('chai')
import bloom = require('../src/utils/bloom')
const assert = chai.assert

let addressTests = [
  {
    bloom:
      '',
    address: '0xb794f5ea0ba39494ce839613fffba74279579268',
    result: false
  },
  {
    bloom:
      '',
    address: '0x54D2C22A7E422DFC71D09D1557D4EBA6C9997D12',
    result: true
  },
  {
    bloom:
      '',
    address: '0x54d2c22a7e422dfc71d09d1557d4eba6c9997d13',
    result: false
  }
]

describe('lib/utils/bloom', function() {
  describe('testAddress', function() {
    addressTests.forEach(function(test) {
      it('test bloom ' + test.bloom, () => {
        assert.equal(bloom.testAddress(test.bloom, test.address), test.result)
      })
    })
  })
})
